// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.6.1;

library AddressUtils {
  function isContract(
    address _addr
  )
    internal
    view
    returns (bool addressCheck)
  {
    uint256 size;

    assembly { size := extcodesize(_addr) } // solhint-disable-line
    addressCheck = size > 0;
  }

}


contract Escrow{
  enum State { Active, Completed, Refunded}

  address public payer;
  address payable public payee;
  address payable public refundee;
  uint public endDate;
  uint public amount;

  event Deposited(uint indexed TransactionId, address indexed payee, uint Tokens);
  event Withdrawn(uint indexed TransactionId, address indexed payee, uint Tokens);
  event RefundsClosed();
  event RefundsEnabled();

  State private _state;
    address payable private _beneficiary;


  constructor(
    address payable _payee,
    uint _amount)
   public {
    payer = msg.sender;
    refundee = refundee;
    payee = _payee;
    amount = _amount;
  }

    // Only payer can send money to Escrow
    // address(this) is contact address
    // address(this).balance is msg.value , send by payer

  function state() public view virtual returns (State) {
        return _state;
    }

  function deposit() payable public virtual {
    require(msg.sender == payer, 'Sender must be the payer');
    require(address(this).balance <= amount, 'Cant send more than escrow amount');
    amount = msg.value;
  }

    //lawyer release the funds
  function release() public virtual {
    require(address(this).balance == amount, 'cannot release funds before full amount is sent');
    require(block.timestamp < endDate, 'cannot release after target date');
     _state = State.Completed;
      payee.transfer(amount);
  }

  function balanceOf() view public virtual returns(uint) {
    return address(this).balance;
  }

  function enableRefunds() public virtual {
        require(state() == State.Active, "RefundEscrow: can only enable refunds while active");
        require(address(this).balance < amount, 'Target not reached');
        _state = State.Refunded;
        emit RefundsEnabled();
    }

  function refund() public payable virtual {
     require(address(this).balance < amount, 'refund if goals are not reached');
     refundee.transfer(amount);
     _state = State.Refunded;
   }


}
